import React from 'react';
import Nav from 'react-bootstrap';
import './App.css';
var DisplayList = require('./DisplayList.js');

var FilteredList = React.createClass({
  // constructor(props) {
  //   super(props);
  //   this.state = { state: "all"};
  //   this.filteredList = [];
  //   // this.listRef = React.createRef();
  // }
  render: function() {
    return (
      <>
        {/* <Nav.Item><Nav.Link eventKey="All" onSelect={this.onSelectFilterSize}>All</Nav.Link></Nav.Item> */}
        <DisplayList list={this.props.list.filter(this.matchesFilterSize)}/>
      </>
    );
  },
  onSelectFilterSize: function(event) {
    // sets the state to the selected size, 
    this.setState({size: event});
    // function passed to the onSelect event handler of HTML buttons
  } ,
  matchesFilterSize: function(item) {
    if (this.state.size === "all") { 
      return true
    } else if (this.state.size === item.size) {
      return true
    } else {
      return false
    }
  }

})

module.exports = FilteredList;
