import React, { Component, useState } from "react";
import Nav from "react-bootstrap/Nav";
import DisplayList from "./DisplayList.js";
import { FaShoppingCart } from "react-icons/fa";

export default class FilteredList extends Component {
  constructor(props) {
    super(props);
    const [size, setSize] = useState("All");
    const [light, setLight] = useState("All");
    const [cartCnt, setCartCnt] = useState(0);
    // this.state = {
    //   size: "All",
    //   light: "All",
    //   cnt: 0
    // };
    // this.list = [];
  }

  onSelectFilterSize = event => {
    // this.setState({
    //   size: event,
    //   light: this.state.light,
    //   cnt: this.state.cnt
    // });
    this.setSize(event)
  };

  matchesFilterSize = item => {
    if (this.state.size === "All") {
      return true;
    } else if (this.state.size === item.size) {
      return true;
    } else {
      return false;
    }
  };

  onSelectFilterLight = event => {
    // this.setState({ size: this.state.size, light: event, cnt: this.state.cnt });
    this.setLight(event);
  };

  matchesFilterLight = item => {
    if (this.state.light === "All") {
      return true;
    } else if (this.state.light === item.light) {
      return true;
    } else {
      return false;
    }
  };

  onPlus = item => {
    // let i;
    // let list = [];
    // for (i; i < this.state.productList; i++) {
    //   if (this.state.productList[i] === item) {
    //     list = this.state.productList;
    //     list[i].cnt++;
    //     this.setState({ productList: list });
    //   }
    // }
    // var count = this.state.cnt + 1;
    // this.setState({
    //   size: this.state.size,
    //   light: this.state.ligth,
    //   cnt: count
    // });
  };

  onMinus = item => {
    // let i;
    // let list = [];
    // for (i; i < this.state.productList; i++) {
    //   if (this.state.productList[i] === item) {
    //     list = this.state.productList;
    //     if (list[i].cnt > 0) {
    //       list[i].cnt--;
    //     }
    //     this.setState({ productList: list });
    //   }
    // }
    // var count = this.state.cnt - 1;
    // this.setState({
    //   size: this.state.size,
    //   light: this.state.ligth,
    //   cnt: count
    // });
  };


  // useEffect(() => {
  // }, []);

  containerStyle = {};

  leftPanelStyle = {
    position: "fixed",
    display: "flex",
    flexDirection: "column",
    width: 350,
    height: 200,
    marginLeft: 1200,
    marginTop: 30,
    backgroundColor: "white",
    borderRadius: 8
  };

  centerPanerStyle = {
    display: "block",
    marginLeft: "auto",
    marginRight: "auto"
  };

  titleStyle = { color: "black", fontFamily: "papyrus", fontSize: 60 };

  navStyle = {
    display: "flex",
    padding: 20,
    gap: 15
  };

  navLinkStyle = {
    // color: onSelect ? "#568203" : "black",
    color: "black",
    textDecoration: "none"
  };

  selectStyle = { border: "none", fontSize: 15 };

  cartIconStyle = {
    display: "flex",
    position: "fixed",
    marginLeft: 350,
    marginTop: 30
  };

  render() {
    return (
      <React.Fragment>
        <div style={this.leftPanelStyle}>
          <Nav style={this.navStyle}>
            <Nav.Item>
              <b>Size:</b>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link
                eventKey="All"
                onSelect={this.onSelectFilterSize}
                style={this.navLinkStyle}
              >
                All
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link
                eventKey="Medium"
                onSelect={this.onSelectFilterSize}
                style={this.navLinkStyle}
              >
                Medium
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link
                eventKey="Large"
                onSelect={this.onSelectFilterSize}
                style={this.navLinkStyle}
              >
                Large
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link
                eventKey="Desktop"
                onSelect={this.onSelectFilterSize}
                style={this.navLinkStyle}
              >
                Desktop
              </Nav.Link>
            </Nav.Item>
          </Nav>
          <Nav style={this.navStyle}>
            <Nav.Item>
              <b>Light:</b>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link
                eventKey="All"
                onSelect={this.onSelectFilterLight}
                style={this.navLinkStyle}
              >
                All
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link
                eventKey="Low Light"
                onSelect={this.onSelectFilterLight}
                style={this.navLinkStyle}
              >
                Low Light
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link
                eventKey="Medium Light"
                onSelect={this.onSelectFilterLight}
                style={this.navLinkStyle}
              >
                Medium Light
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link
                eventKey="Bright Light"
                onSelect={this.onSelectFilterLight}
                style={this.navLinkStyle}
              >
                Bright Light
              </Nav.Link>
            </Nav.Item>
          </Nav>
          <div style={this.navStyle}>
            <div>
              <b>Sort:</b>
            </div>
            <select style={this.selectStyle}>
              <option selected>All</option>
              <option value="low2high">Lowest to high</option>
              <option value="high2low">Highest to Low</option>
            </select>
          </div>
        </div>
        <div style={this.cartIconStyle}>
          <FaShoppingCart></FaShoppingCart>
          {this.state.cnt > 0 && <p>{this.state.cnt}</p>}
        </div>
        <div style={this.containerStyle}>
          <div style={this.centerPanelStyle}>
            <h1 style={this.titleStyle}>CS1300 Plant Shop</h1>
            <DisplayList
              list={this.props.list}
              matchesSize={this.matchesFilterSize}
              matchesLight={this.matchesFilterLight}
              // onPlus={this.props.onPlus}
              // onMinus={this.props.onMinus}
              onPlus={this.onPlus}
              onMinus={this.onMinus}
            />
          </div>
        </div>
      </React.Fragment>
    );
  }
}
