import React, { Component } from "react";
import Nav from "react-bootstrap/Nav";
import DisplayList from "./DisplayList.js";

export default class FilteredList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      size: "All"
    };
    this.list = props.list;
    // this.listRef = React.createRef();
  }

  onSelectFilterSize = event => {
    // sets the state to the selected size,
    this.setState({ size: event });
  };

  matchesFilterSize = item => {
    if (this.state.size === "All") {
      return true;
    } else if (this.state.size === item.size) {
      return true;
    } else {
      return false;
    }
  };

  containerStyle = { display: "flex", gap: 20 };

  leftPanelStyle = {
    display: "flex",
    flexDirection: "column",
    margin: 45,
    // backgroundColor: "#a5daa9",
    backgroundColor: "white",
    borderRadius: 5,
  };

  centerPanerStyle = {
    display: "block",
    marginLeft: "auto",
    marginRight: "auto"
  };

  titleStyle = { color: "black", fontFamily: "papyrus", fontSize: 60 };

  navStyle = {
    display: "flex",
    padding: 20,

    gap: 15,
    marginLeft: "auto",
    marginRight: "auto"
  };

  render() {
    return (
      <React.Fragment>
        <div style={this.containerStyle}>
          <div style={this.leftPanelStyle}>
            <Nav style={this.navStyle}>
              <Nav.Item>
                <b>Size:</b>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="All" onSelect={this.onSelectFilterSize}>
                  All
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="Medium" onSelect={this.onSelectFilterSize}>
                  Medium
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="Large" onSelect={this.onSelectFilterSize}>
                  Large
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="Desktop" onSelect={this.onSelectFilterSize}>
                  Desktop
                </Nav.Link>
              </Nav.Item>
            </Nav>
          </div>
          <div style={this.centerPanelStyle}>
            <h1 style={this.titleStyle}>CS1300 Plant Shop</h1>
            <DisplayList
              list={this.props.list.filter(this.matchesFilterSize)}
            />
          </div>
        </div>
      </React.Fragment>
    );
  }
}
