import React, { Component } from "react";

export default class PlantItem extends Component {
  constructor(props) {
    super(props);
    // this.filteredList = props.list;
    // this.cartList = props.cart;
  }

  itemStyle = {
    display: "flex",
    flexDirection: "column",
    textAlign: "center",
    height: 470,
    backgroundColor: "white",
    borderRadius: 5,
    gap: 8
  };

  imgStyle = { width: 200, borderTopRightRadius: 8, borderTopLeftRadius: 8 };

  imgSrc =
    "https://www.gardeningknowhow.com/wp-content/uploads/2012/03/houseplant-sansevieria.jpg";

  plantNameStyle = { marginTop: 5, fontSize: 25 };

  plantSizeStyle = { fontStyle: "italic", fontSize: 18 };

  plantLightStyle = {};

  plantPriceStyle = { fontSize: 25 };

  addPlantButtonStyle = {
    width: 70,
    height: 30,
    marginLeft: "auto",
    marginRight: "auto",
    borderColor: "none",
    borderRadius: 8,
    backgroundColor: "#568203"
  };

  onAdd = item => {
    item.cnt = 1;
    // item.cnt++;
  };

  onPlus = item => {
    // item.cnt++;
  };

  onMinus = item => {
    // item.cnt--;
  };

  plantCntStyle = {
    display: "flex",
    marginLeft: "auto",
    marginRight: "auto"
  };

  plantCntButtonStyle = {
    width: 30,
    height: 30,
    color: "white",
    // marginLeft: "auto",
    // marginRight: "auto",
    borderColor: "none",
    borderRadius: 5,
    backgroundColor: "#568203"
  };

  render() {
    return (
      <div style={this.itemStyle}>
        <img style={this.imgStyle} src={this.imgSrc} alt=""></img>
        <div style={this.plantNameStyle}>
          <b>{props.item.name}</b>
        </div>
        <div style={this.plantSizeStyle}>{props.item.size}</div>
        <div style={this.plantLightStyle}>{props.item.light}</div>
        <div style={this.plantPriceStyle}>${props.item.price}</div>
        {!props.item.added && (
          <button style={this.addPlantButtonStyle} onclick={this.onAdd(props.item)}>
            Add
          </button>
        )}
        {props.item.added && (
          <div style={this.plantCntStyle}>
            <button
              style={this.plantCntButtonStyle}
              onclick={this.onMinus(props.item)}
            >
              <b>-</b>
            </button>
            <div style={{ height: 30, width: 30 }}>{props.item.cnt}</div>
            <button
              style={this.plantCntButtonStyle}
              onclick={this.onPlus(props.item)}
            >
              <b>+</b>
            </button>
          </div>
        )}
      </div>
    );
  }
}
