import React, { Component } from "react";
import Nav from "react-bootstrap/Nav";
import DisplayList from "./DisplayList.js";
import Cart from "./Cart.js";

export default class FilteredList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      size: "All",
      light: "All",
      sort: "All",
      totalPrice: 0
    };
    this.cartList = [];
    // this.totalPrice = 0;
  }

  onSelectFilterSize = event => {
    this.setState({
      size: event,
      light: this.state.light,
      sort: this.state.sort
    });
  };

  matchesFilterSize = item => {
    if (this.state.size === "All") {
      return true;
    } else if (this.state.size === item.size) {
      return true;
    } else {
      return false;
    }
  };

  onSelectFilterLight = event => {
    this.setState({
      size: this.state.size,
      light: event,
      sort: this.state.sort
    });
  };

  matchesFilterLight = item => {
    if (this.state.light === "All") {
      return true;
    } else if (this.state.light === item.light) {
      return true;
    } else {
      return false;
    }
  };

  onSort = value => {
    this.setState({
      size: this.state.size,
      light: this.state.light,
      sort: value
    });
  };

  matchesSort = (a, b) => {
    if (this.state.sort === "all") {
      return a;
    } else if (this.state.sort === "high2low") {
      return a.price - b.price;
    } else if (this.state.sort === "low2high") {
      return b.price - a.price;
    }
  };

  onPlus = item => {
    // this.cartList.push( item );

    this.setState({
      cartList: this.state.cartList.push(item),
      totalPrice: this.state.totalPrice + item.price
    });
  };

  onMinus = item => {
    let i;
    let list = [];
    for (i; i < this.cartList; i++) {
      if (this.cartList[i] === item) {
        this.cartList.splice(i);
        this.setState({ totalPrice: this.state.totalPrice - item.price });
      }
    }
  };

  containerStyle = { display: "flex" };

  leftPanelStyle = {
    marginLeft: 30,
    marginTop: 30,
    height: 500,
    backgroundColor: "#869b46",
    borderRadius: 8
  };

  centerPanerStyle = {};

  titleStyle = { color: "black", fontFamily: "papyrus", fontSize: 60 };

  navStyle = {
    display: "flex",
    flexDirection: "column",
    padding: 20,
    gap: 15
    // backgroundColor: "#739900"
  };

  navLinkStyle = {
    // color: onSelect ? "#568203" : "black",
    color: "black",
    textDecoration: "none"
  };

  selectStyle = { border: "none", fontSize: 15 };

  render() {
    return (
      <div style={this.containerStyle}>
        <div id="leftPanel" style={this.leftPanelStyle}>
          <Nav style={this.navStyle} onSelect={this.onSelectFilterSize}>
            <Nav.Item>
              <b>Size:</b>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="All" style={this.navLinkStyle}>
                All
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="Medium" style={this.navLinkStyle}>
                Medium
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="Large" style={this.navLinkStyle}>
                Large
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="Desktop" style={this.navLinkStyle}>
                Desktop
              </Nav.Link>
            </Nav.Item>
          </Nav>
          <Nav style={this.navStyle} onSelect={this.onSelectFilterLight}>
            <Nav.Item>
              <b>Light:</b>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="All" style={this.navLinkStyle}>
                All
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="Low Light" style={this.navLinkStyle}>
                Low Light
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="Medium Light" style={this.navLinkStyle}>
                Medium Light
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="Bright Light" style={this.navLinkStyle}>
                Bright Light
              </Nav.Link>
            </Nav.Item>
          </Nav>
          <div style={this.navStyle}>
            <div>
              <b>Sort:</b>
            </div>
            <select style={this.selectStyle} onChange={this.onSort}>
              <option value="all">Choose</option>
              <option value="low2high">Lowest to high</option>
              <option value="high2low">Highest to Low</option>
            </select>
          </div>
        </div>

        <div id="centerPanel">
          <h1 style={this.titleStyle}>CS1300 Plant Shop</h1>
          <DisplayList
            list={this.props.list}
            matchesSize={this.matchesFilterSize}
            matchesLight={this.matchesFilterLight}
            matchesSort={this.matchesSort}
            onPlus={this.onPlus}
            onMinus={this.onMinus}
          />
        </div>
        <div id="rightPanel">
          <Cart
            totalPrice={this.state.totalPrice}
            cartList={this.cartList}
            onPlus={this.onPlus}
            onMinus={this.onMinus}
          />
        </div>
      </div>
    );
  }
}
