import React, { Component } from "react";
import Nav from "react-bootstrap/Nav";
import Dropdown from "react-bootstrap/Dropdown";
import DisplayList from "./DisplayList.js";

export default class FilteredList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      size: "All",
      light: "All"
    };
    this.list = props.list;
  }

  onSelectFilterSize = event => {
    this.setState({ size: event, light: this.state.light });
  };

  matchesFilterSize = item => {
    if (this.state.size === "All") {
      return true;
    } else if (this.state.size === item.size) {
      return true;
    } else {
      return false;
    }
  };

  onSelectFilterLight = event => {
    this.setState({ size: this.state.size, light: event });
  };

  matchesFilterLight = item => {
    if (this.state.light === "All") {
      return true;
    } else if (this.state.light === item.light) {
      return true;
    } else {
      return false;
    }
  };

  containerStyle = { display: "flex", gap: 20 };

  leftPanelStyle = {
    display: "flex",
    flexDirection: "column",
    width: 350,
    height: 200,
    margin: 45,
    // backgroundColor: "#a5daa9",
    backgroundColor: "white",
    borderRadius: 5
  };

  centerPanerStyle = {
    display: "block",
    marginLeft: "auto",
    marginRight: "auto"
  };

  titleStyle = { color: "black", fontFamily: "papyrus", fontSize: 60 };

  navStyle = {
    display: "flex",
    padding: 20,
    gap: 15
  };

  selectStyle = { border: "none" };

  render() {
    return (
      <React.Fragment>
        <div style={this.containerStyle}>
          <div style={this.leftPanelStyle}>
            <Nav style={this.navStyle}>
              <Nav.Item>
                <b>Size:</b>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link
                  eventKey="All"
                  onSelect={this.onSelectFilterSize}
                  style={{ color: "black" }}
                >
                  All
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link
                  eventKey="Medium"
                  onSelect={this.onSelectFilterSize}
                  style={{ color: "black" }}
                >
                  Medium
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link
                  eventKey="Large"
                  onSelect={this.onSelectFilterSize}
                  style={{ color: "black" }}
                >
                  Large
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link
                  eventKey="Desktop"
                  onSelect={this.onSelectFilterSize}
                  style={{ color: "black" }}
                >
                  Desktop
                </Nav.Link>
              </Nav.Item>
            </Nav>
            <Nav style={this.navStyle}>
              <Nav.Item>
                <b>Light:</b>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link
                  eventKey="All"
                  onSelect={this.onSelectFilterLight}
                  style={{ color: "black" }}
                >
                  All
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link
                  eventKey="Low Light"
                  onSelect={this.onSelectFilterLight}
                  style={{ color: "black" }}
                >
                  Low Light
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link
                  eventKey="Medium Light"
                  onSelect={this.onSelectFilterLight}
                  style={{ color: "black" }}
                >
                  Medium Light
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link
                  eventKey="BrightLight"
                  onSelect={this.onSelectFilterLight}
                  style={{ color: "black" }}
                >
                  Bright Light
                </Nav.Link>
              </Nav.Item>
            </Nav>
            <div style={this.navStyle}>
              <div>
                <b>Sort:</b>
              </div>
              <select style={this.selectStyle} class="mb-lg">
                <option selected>All</option>
                <option value="low2high">Lowest to high</option>
                <option value="high2low">Highest to Low</option>
              </select>
            </div>
          </div>
          <div style={this.centerPanelStyle}>
            <h1 style={this.titleStyle}>CS1300 Plant Shop</h1>
            <DisplayList
              list={this.props.list
                .filter(this.matchesFilterSize)
                .filter(this.matchesFilterLight)}
            />
          </div>
        </div>
      </React.Fragment>
    );
  }
}
