import React, { Component } from "react";

export default class PlantItem extends Component {
  constructor(props) {
    super(props);
    // this.filteredList = props.list;
    // this.cartList = props.cart;
  }
  style = {}


  render() {
    return (
      <div style={this.itemStyle}>
        <img style={this.imgStyle} src={this.imgSrc} alt=""></img>
        <div style={this.plantNameStyle}>
          <b>{this.props.item.name}</b>
        </div>
        <div style={this.plantSizeStyle}>{this.props.item.size}</div>
        <div style={this.plantLightStyle}>{this.props.item.light}</div>
        <div style={this.plantPriceStyle}>${this.props.item.price}</div>
        {this.props.item.cnt == 0 && (
          <button
            style={this.addPlantButtonStyle}
            onclick={this.props.onPlus(this.props.item)}
          >
            Add
          </button>
        )}
        {this.props.item.cnt > 0 && (
          <div style={this.plantCntStyle}>
            <button
              style={this.plantCntButtonStyle}
              onclick={this.props.onMinus(this.props.item)}
            >
              <b>-</b>
            </button>
            <div style={{ height: 30, width: 30 }}>{this.props.item.cnt}</div>
            <button
              style={this.plantCntButtonStyle}
              onclick={this.props.onPlus(this.props.item)}
            >
              <b>+</b>
            </button>
          </div>
        )}
      </div>
    );
  }
}
