import React, { Component } from "react";
import PlantItem from "./PlantItem";

export default class DisplayList extends Component {
  constructor(props) {
    super(props);
    this.filteredList = props.list
      .filter(this.props.matchesSize)
      .filter(this.props.matchesLight);
    this.cartList = props.cart;
  }

  listStyle = {
    display: "flex",
    flexWrap: "wrap",
    width: 850,
    textAlign: "center",
    gap: 45,
    marginLeft: "auto",
    marginRight: "auto",
    justifyContent: "center",
    paddingBottom: 40
  };

  itemStyle = {
    display: "flex",
    flexDirection: "column",
    textAlign: "center",
    height: 470,
    backgroundColor: "white",
    borderRadius: 5,
    gap: 8
  };

  imgStyle = { width: 200, borderTopRightRadius: 8, borderTopLeftRadius: 8 };

  imgSrc =
    "https://www.gardeningknowhow.com/wp-content/uploads/2012/03/houseplant-sansevieria.jpg";

  plantNameStyle = { marginTop: 5, fontSize: 25 };

  plantSizeStyle = { fontStyle: "italic", fontSize: 18 };

  plantLightStyle = {};

  plantPriceStyle = { fontSize: 25 };

  addPlantButtonStyle = {
    width: 70,
    height: 30,
    marginLeft: "auto",
    marginRight: "auto",
    borderColor: "none",
    borderRadius: 8,
    backgroundColor: "#568203"
  };

