    width: 30,
    height: 30,
    color: "white",
    // marginLeft: "auto",
    // marginRight: "auto",
    borderColor: "none",
    borderRadius: 5,
    backgroundColor: "#568203"
  };

  render() {
    return (
      <div style={this.listStyle}>
