import React, { Component } from "react";

export default class DisplayList extends Component {
  constructor(props) {
    super(props);
    this.filteredList = props.list;
  }

  listStyle = { padding: 10, justifyContent: "center" };
  itemStyle = { display: "flex", gap: 5 };

  render() {
    return (
      <div style={this.listStyle}>
        {this.filteredList.map(item => (
          <div style={this.itemStyle}>
            <div>
              <b>{item.name}</b>
            </div>
            <div>{item.size}</div>
          </div>
        ))}
      </div>
    );
  }
}
