import React, { Component } from "react";

export default class DisplayList extends Component {
  constructor(props) {
    super(props);
    this.filteredList = props.list;
  }

  listStyle = {
    display: "flex",
    flexWrap: "wrap",
    width: 600,
    marginLeft: 50,
    textAlign: "center",
    gap: 45,
    marginLeft: "auto",
    marginRight: "auto",
    justifyContent: "center"
  };

  itemStyle = {
    display: "flex",
    flexDirection: "column",
    textAlign: "center",
    height: 320,
    backgroundColor: "white",
    borderRadius: 5,
    gap: 5
  };

  imgStyle = { height: 200, borderTopRightRadius: 5, borderTopLeftRadius: 5 };

  imgSrc =
    "https://www.gardeningknowhow.com/wp-content/uploads/2012/03/houseplant-sansevieria.jpg";

  plantNameStyle = { marginTop: 5 };

  plantSizeStyle = { fontStyle: "italic" };

  plantLightStyle = {};

  itemButtonStyle = {
    width: 70,
    height: 30,
    marginLeft: "auto",
    marginRight: "auto",
    borderColor: "none",
    borderRadius: 5,
    backgroundColor: "#568203"
  };

  render() {
    return (
      <div style={this.listStyle}>
        {this.filteredList.map(item => (
          <div style={this.itemStyle}>
            <img style={this.imgStyle} src={this.imgSrc} alt=""></img>
            <div style={this.plantNameStyle}>
              <b>{item.name}</b>
            </div>
            <div style={this.plantSizeStyle}>{item.size}</div>
            <div style={this.plantLightStyle}>{item.light}</div>
            <div></div><button style={this.itemButtonStyle}>Add</button>
          </div>
        ))}
      </div>
    );
  }
}
