import React, { Component } from 'react'
import Nav from 'react-bootstrap/Nav'
import './App.css'
import DisplayList  from './DisplayList.js'

export default class FilteredList extends Component {
  constructor(props) {
    super(props)
    this.state = { state: "all"}
    // this.filteredList = [];
    // this.listRef = React.createRef();
  }

  onSelectFilterSize(event) {
    // sets the state to the selected size, 
    this.setState({size: event})
    // function passed to the onSelect event handler of HTML buttons
  } 

  matchesFilterSize(item) {
    if (this.state.size === "all") { 
      return true
    } else if (this.state.size === item.size) {
      return true
    } else {
      return false
    }

  }
  render() {
    return (
      <React.Fragment>
        <Nav>
          <Nav.Item>
            <Nav.Link eventKey="All" onSelect={this.onSelectFilterSize}>
              All
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="Medium" onSelect={this.onSelectFilterSize}>
              All
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="Large" onSelect={this.onSelectFilterSize}>
              All
            </Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="Desktop" onSelect={this.onSelectFilterSize}>
              All
            </Nav.Link>
          </Nav.Item>
        </Nav>
        <DisplayList list={ this.props.list.filter(this.matchesFilterSize) }/>
      </React.Fragment>
    )
  }
}
