import React, { Component } from "react";

export default class DisplayList extends Component {
  constructor(props) {
    super(props);
    // this.filteredList = props.list;
    this.cartList = props.cart;
  }

  listStyle = {
    display: "flex",
    flexWrap: "wrap",
    width: 850,
    textAlign: "center",
    gap: 45,
    marginLeft: "auto",
    marginRight: "auto",
    justifyContent: "center",
    paddingBottom: 40
  };

  itemStyle = {
    display: "flex",
    flexDirection: "column",
    textAlign: "center",
    height: 470,
    backgroundColor: "white",
    borderRadius: 5,
    gap: 8
  };

  imgStyle = { width: 200, borderTopRightRadius: 8, borderTopLeftRadius: 8 };

  imgSrc =
    "https://www.gardeningknowhow.com/wp-content/uploads/2012/03/houseplant-sansevieria.jpg";

  plantNameStyle = { marginTop: 5, fontSize: 25 };

  plantSizeStyle = { fontStyle: "italic", fontSize: 18 };

  plantLightStyle = {};

  plantPriceStyle = { fontSize: 25 };

  addPlantButtonStyle = {
    width: 70,
    height: 30,
    marginLeft: "auto",
    marginRight: "auto",
    borderColor: "none",
    borderRadius: 8,
    backgroundColor: "#568203"
  };

  onPlus = item => {
    item.cnt++;
  }

  plantCntStyle = {
    display: "flex",
    marginLeft: "auto",
    marginRight: "auto"
  };

  plantCntButtonStyle = {
    width: 30,
    height: 30,
    color: "white",
    // marginLeft: "auto",
    // marginRight: "auto",
    borderColor: "none",
    borderRadius: 5,
    backgroundColor: "#568203"
  };

  render() {
    return (
      <div style={this.listStyle}>
        {/* {this.filteredList.map(item => ( */}
        {this.props.list.map(item => (
          <div style={this.itemStyle}>
            <img style={this.imgStyle} src={this.imgSrc} alt=""></img>
            <div style={this.plantNameStyle}>
              <b>{item.name}</b>
            </div>
            <div style={this.plantSizeStyle}>{item.size}</div>
            <div style={this.plantLightStyle}>{item.light}</div>
            <div style={this.plantPriceStyle}>${item.price}</div>
            {item.added && (
              <button style={this.addPlantButtonStyle}>Add</button>
            )}
            {!item.added && (
              <div style={this.plantCntStyle}>
                <button style={this.plantCntButtonStyle} onclick={onPl}>
                  <b>-</b>
                </button>
                <div style={{ height: 30, width: 30 }} onclick={item.cnt--}>{item.cnt}</div>
                <button style={this.plantCntButtonStyle}>
                  <b>+</b>
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    );
  }
}
