import "./App.css";
import React, { Component } from "react";
import "./FilteredList.js";
import FilteredList from "./FilteredList";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      productList: [
        {
          name: "Cute Plant",
          size: "Medium",
          light: "Low Light",
          price: 34,
          added: false,
          cnt: 0
        },
        {
          name: "Happy Plant",
          size: "Large",
          light: "Medium Light",
          price: 23,
          added: false,
          cnt: 0
        },
        {
          name: "Sad Plant",
          size: "Desktop",
          light: "Bright Light",
          price: 44,
          added: false,
          cnt: 0
        },
        {
          name: "Smart Plant",
          size: "Medium",
          light: "Bright Light",
          price: 22,
          added: false,
          cnt: 0
        },
        {
          name: "Sleepy Plant",
          size: "Desktop",
          light: "Bright Light",
          price: 80,
          added: false,
          cnt: 0
        },
        {
          name: "Crazy Plant",
          size: "Medium",
          light: "Low Light",
          price: 16,
          added: false,
          cnt: 0
        },
        {
          name: "Silly Plant",
          size: "Large",
          light: "Medium Light",
          price: 34,
          added: false,
          cnt: 0
        },
        {
          name: "Funky Plant",
          size: "Desktop",
          light: "Medium Light",
          price: 55,
          added: false,
          cnt: 0
        },
        {
          name: "Green Plant",
          size: "Medium",
          light: "Low Light",
          price: 36,
          added: false,
          cnt: 0
        },
        {
          name: "Rainbow Plant",
          size: "Medium",
          light: "Low Light",
          price: 26,
          added: false,
          cnt: 0
        },
        {
          name: "Old Plant",
          size: "Large",
          light: "Low Light",
          price: 67,
          added: false,
          cnt: 0
        },
        {
          name: "Baby Plant",
          size: "Desktop",
          light: "Medium Light",
          price: 15,
          added: false,
          cnt: 0
        }
      ]
    };
  }

  onPlus = item => {
    let i;
    let list = [];
    for (i; i < this.state.productList; i++) {
      if (this.state.productList[i].item === item) {
        list = this.state.productList
        list[i].item.cnt++;
        this.setState({productList: list});
      }
    }
  };

  onMinus = item => {
    let i;
    let list = [];
    for (i; i < this.state.productList; i++) {
      if (this.state.productList[i].item === item) {
        list = this.state.productList
        list[i].item.cnt--;
        this.setState({productList: list});
      }
    }
  };

  render() {
    return (
      <div className="App">
        <FilteredList
          list={this.state.productList}
          onPlus={this.onPlus}
          onMinus={this.onMinus}
        ></FilteredList>
      </div>
    );
  }
}
