import React, { Component } from "react";

export default class DisplayList extends Component {
  constructor(props) {
    super(props);
    this.filteredList = props.list;
  }

  listStyle = {
    display: "flex",
    flexWrap: "wrap",
    width: 600,
    marginLeft: 50,
    textAlign: "center",
    gap: 35,
    marginLeft: "auto",
    marginRight: "auto",
    justifyContent: "center"
  };

  itemStyle = {
    display: "flex",
    flexDirection: "column",
    textAlign: "center",
    height: 270,
    backgroundColor: "white",
    borderRadius: 5
  };

  imgStyle = { height: 200, borderTopRightRadius: 5, borderTopLeftRadius: 5 };

  imgSrc =
    "https://www.gardeningknowhow.com/wp-content/uploads/2012/03/houseplant-sansevieria.jpg";

  render() {
    return (
      <div style={this.listStyle}>
        {this.filteredList.map(item => (
          <div style={this.itemStyle}>
            <img style={this.imgStyle} src={this.imgSrc} alt=""></img>
            <div>
              <b>{item.name}</b>
            </div>
            <div>{item.size}</div>
          </div>
        ))}
      </div>
    );
  }
}
