import React, { Component } from "react";
import Nav from "react-bootstrap/Nav";
import DisplayList from "./DisplayList.js";
import Cart from "./Cart.js";

export default class FilteredList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      size: "All",
      light: "All",
      sort: "All"
    };
    this.cartList = [];
    this.totalPrice = 0;
  }

  onSelectFilterSize = event => {
    this.setState({
      size: event,
      light: this.state.light
    });
  };

  matchesFilterSize = item => {
    if (this.state.size === "All") {
      return true;
    } else if (this.state.size === item.size) {
      return true;
    } else {
      return false;
    }
  };

  onSelectFilterLight = event => {
    this.setState({ size: this.state.size, light: event, sort: this.state.sort });
  };

  matchesFilterLight = item => {
    if (this.state.light === "All") {
      return true;
    } else if (this.state.light === item.light) {
      return true;
    } else {
      return false;
    }
  };

  onSort = event => {
    this.setState({
      size: this.state.size,
      light: this.state.light,
      sort: event
    });
  };

  matchesSort = item => {
    if (this.state.light === "All") {
      return true;
    } else if (this.state.sort === item.light) {
      return true;
    } else {
      return false;
    }
  };

  onPlus = item => {
    // this.cartList.push({
    //   name: "Cute Plant",
    //   size: "Medium",
    //   light: "Low Light",
    //   price: 34,
    //   cnt: 0
    // });
    this.cartList.push({ item });
  };

  onMinus = item => {
    let i;
    let list = [];
    for (i; i < this.cartList; i++) {
      if (this.cartList[i] === item) {
        this.cartList.splice(i);
      }
    }
  };

  // useEffect(() => {
  // }, []);

  containerStyle = { display: "flex" };

  leftPanelStyle = {
    marginLeft: 30,
    marginTop: 30,
    backgroundColor: "white",
    height: 100
  };

  centerPanerStyle = {};

  titleStyle = { color: "black", fontFamily: "papyrus", fontSize: 60 };

  navStyle = {
    display: "flex",
    flexDirection: "column",
    padding: 20,
    gap: 15,
    backgroundColor: "white"
  };

  navLinkStyle = {
    // color: onSelect ? "#568203" : "black",
    color: "black",
    textDecoration: "none"
  };

  selectStyle = { border: "none", fontSize: 15 };

  render() {
    return (
      <div style={this.containerStyle}>
        <div id="leftPanel" style={this.leftPanelStyle}>
          <Nav style={this.navStyle} onSelect={this.onSelectFilterSize}>
            <Nav.Item>
              <b>Size:</b>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="All" style={this.navLinkStyle}>
                All
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="Medium" style={this.navLinkStyle}>
                Medium
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="Large" style={this.navLinkStyle}>
                Large
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="Desktop" style={this.navLinkStyle}>
                Desktop
              </Nav.Link>
            </Nav.Item>
          </Nav>
          <Nav style={this.navStyle} onSelect={this.onSelectFilterLight}>
            <Nav.Item>
              <b>Light:</b>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="All" style={this.navLinkStyle}>
                All
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="Low Light" style={this.navLinkStyle}>
                Low Light
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="Medium Light" style={this.navLinkStyle}>
                Medium Light
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="Bright Light" style={this.navLinkStyle}>
                Bright Light
              </Nav.Link>
            </Nav.Item>
          </Nav>
          <div style={this.navStyle}>
            <div>
              <b>Sort:</b>
            </div>
            <select style={this.selectStyle}>
              <option selected>All</option>
              <option value="low2high">Lowest to high</option>
              <option value="high2low">Highest to Low</option>
            </select>
          </div>
        </div>

        <div id="centerPanel">
          <h1 style={this.titleStyle}>CS1300 Plant Shop</h1>
          <DisplayList
            list={this.props.list}
            matchesSize={this.matchesFilterSize}
            matchesLight={this.matchesFilterLight}
            onPlus={this.onPlus}
            onMinus={this.onMinus}
          />
        </div>
        <div id="rightPanel">
          <Cart
            totalPrice={this.totalPrice}
            cartList={this.cartList}
            onPlus={this.onPlus}
            onMinus={this.onMinus}
          />
        </div>
      </div>
    );
  }
}
